# attribute manipulation for ADP (ARGG)
# Does 2 things:
#  1. Set ApplicationID and CompanyID from proper entitlement values
#  2. Set NameId from employeeid in sPUC
#

# attribute names
$attr_epe  = 'urn:mace:dir:attribute-def:eduPersonEntitlement';
$attr_sho  = 'urn:mace:terena.org:attribute-def:schacHomeOrganization';
$attr_spuc = 'urn:schac:attribute-def:schacPersonalUniqueCode';
$attr_mail = 'urn:mace:dir:attribute-def:mail';

# attribute values
$entitlementADP = 'urn:x-surfnet:adp.com:ADP:';
$spuc_namespace = 'urn:schac:personalUniqueCode:nl:local';

# attributes to let through (ARP)
$requiredAttributes = array(
  'ApplicationID',
  'CompanyID'
);

switch ($attributes[$attr_sho][0]) {
  case 'surfnet.nl':
    $attributes['CompanyID'] = array ("800002.adp");
    $attributes['ApplicationID'] = array ("test");
    $subjectId = $attributes[$attr_mail][0];
    break;

  case 'ou.nl':
  case 'zuyd.nl':

  # set CompanyID and ApplicationID attributes to values specified in 
  # entitlement
  if (isset($attributes[$attr_epe])) 
  {
    foreach ($attributes[$attr_epe] as $entitlementValue) 
    {
      if (strpos($entitlementValue, $entitlementADP) === 0) 
      {
        if (substr($entitlementValue,-4) == '.adp')
          $attributes['CompanyID'] = array( substr($entitlementValue, strlen($entitlementADP)));
        else
          $attributes['ApplicationID'] = array( substr($entitlementValue, strlen($entitlementADP)));
      }
    }
  }
  
  # set nameid based on schacPersonalUniqueCode
  if (!isset($attributes[$attr_sho]) or !isset($attributes[$attr_spuc]))
  {
    $msg = "Required attribute schacHomeOrginization or schacPersonalUniqueCode not set";
    $amFout = new EngineBlock_Attributes_Manipulator_CustomException("Required attribute schacHomeOrginization or schacPersonalUniqueCode not set", EngineBlock_Attributes_Manipulator_CustomException::CODE_NOTICE);
    $amFout->setFeedbackTitle(array("en" => "Missing attributes", "nl" => "Missende attributen"));
    $amFout->setFeedbackDescription(array("en" => $msg, "nl" => $msg));
    throw $amFout;
  }
  
  # construct correct sPUC prefix from sHO
  $sho  = $attributes[$attr_sho][0];
  $spuc_prefix = "{$spuc_namespace}:$sho:employeeid:";
  
  # find sPUC with specified prefix
  $spuc = null;
  foreach ($attributes[$attr_spuc] as $s)
  {
    if (stripos($s,$spuc_prefix)===0)
    {
      $spuc = substr($s,strlen($spuc_prefix));
    }
  }
  
  if ($spuc===null)
  {
    $msg = "Employeeid could not be extracted from schacPersonalUnqiueCode (malformed value?)";
    $amFout = new EngineBlock_Attributes_Manipulator_CustomException($msg, EngineBlock_Attributes_Manipulator_CustomException::CODE_NOTICE);
    $amFout->setFeedbackTitle(array("en" => "No employeeid found", "nl" => "Geen employeeid gevonden"));
    $amFout->setFeedbackDescription(array("en" => $msg, "nl" => $msg));
    throw $amFout;
  }
  
  $subjectId = $spuc;
  
  break;
}


