function arp_check_mandatory_attribute($attributes, $mandatory, $target) {
    if (!array_key_exists($mandatory, $attributes)) {
        /*
        throw new UtilsException(
            'Access Control Error',
            'Toegang tot ' . $target . ' is niet mogelijk met uw account.',
            'Neem contact op met het ICT beheer van uw organisatie om het "' . $mandatory . '" attribuut toe te laten voegen voor uw account.'
        );
        */
        $amException = new EngineBlock_Attributes_Manipulator_CustomException("Kennisnet Federatie Access Control Error (" . $mandatory . ")", EngineBlock_Attributes_Manipulator_CustomException::CODE_NOTICE);
        $amException->setFeedbackTitle(array("nl" => "Toegangs controle fout", "en" => "Access Control Error"));
        $amException->setFeedbackDescription(array(
            "en" => 'Access to ' . $target . ' is not possible with your account. Please contact the ICT departpent of you rorganization to add the ' . $mandatory . ' attribute your your account',
            "nl" => 'Toegang tot ' . $target . ' is niet mogelijk met uw account. Neem contact op met het ICT beheer van uw organisatie om het ' . $mandatory . ' attribuut toe te laten voegen voor uw account.'));
        throw $amException;
    }
    return $attributes[$mandatory];
}

