function attributesContainAttributeWithValue($attrs, $attrName, $valueToFind)
{
    if (isset($attrs) and ($attrs !== FALSE)) {
        if (!empty($attrs[$attrName])) {
            foreach ($attrs[$attrName] as $attributeValue) {
                if ($attributeValue === $valueToFind) {
                    return true;
                }
            }
        }
    }
    return false;
}

